func (uq *UserQuery) loadCreatedObjects(ctx context.Context, query *ObjectQuery, nodes []*User, init func(*User), assign func(*User, *Object)) error {
	fks := make([]driver.Value, 0, len(nodes))
	nodeids := make(map[puuid.ID]*User)
	for i := range nodes {
		fks = append(fks, nodes[i].ID)
		nodeids[nodes[i].ID] = nodes[i]
		if init != nil {
			init(nodes[i])
		}
	}
	query.withFKs = true
	query.Where(predicate.Object(func(s *sql.Selector) {
		s.Where(sql.InValues(user.CreatedObjectsColumn, fks...))
	}))
	neighbors, err := query.All(ctx)
	if err != nil {
		return err
	}
	for _, n := range neighbors {
		fk := n.created_by
		if fk == nil {
			return fmt.Errorf(`foreign-key "created_by" is nil for node %v`, n.ID)
		}
		node, ok := nodeids[*fk]
		if !ok {
			return fmt.Errorf(`unexpected foreign-key "created_by" returned %v for node %v`, *fk, n.ID)
		}
		assign(node, n)
	}
	return nil
}
